'use strict';

var libnexajs = module.exports;

// module information
libnexajs.version = 'v' + require('./package.json').version;
libnexajs.versionGuard = function(version) {
  if (version !== undefined) {
    var message = 'More than one instance of libnexajs-lib found. ' +
      'Please make sure to require libnexajs-lib and check that submodules do' +
      ' not also include their own libnexajs-lib dependency.';
    throw new Error(message);
  }
};
libnexajs.versionGuard(global._libnexajs);
global._libnexajs = libnexajs.version;

// crypto
libnexajs.crypto = {};
libnexajs.crypto.BN = require('./lib/crypto/bn');
libnexajs.crypto.ECDSA = require('./lib/crypto/ecdsa');
libnexajs.crypto.Schnorr = require('./lib/crypto/schnorr');
libnexajs.crypto.Hash = require('./lib/crypto/hash');
libnexajs.crypto.Random = require('./lib/crypto/random');
libnexajs.crypto.Point = require('./lib/crypto/point');
libnexajs.crypto.Signature = require('./lib/crypto/signature');

// encoding
libnexajs.encoding = {};
libnexajs.encoding.Base58 = require('./lib/encoding/base58');
libnexajs.encoding.Base58Check = require('./lib/encoding/base58check');
libnexajs.encoding.BufferReader = require('./lib/encoding/bufferreader');
libnexajs.encoding.BufferWriter = require('./lib/encoding/bufferwriter');
libnexajs.encoding.Varint = require('./lib/encoding/varint');

// utilities
libnexajs.util = {};
libnexajs.util.buffer = require('./lib/util/buffer');
libnexajs.util.js = require('./lib/util/js');
libnexajs.util.preconditions = require('./lib/util/preconditions');
libnexajs.util.base32 = require('./lib/util/base32');
libnexajs.util.convertBits = require('./lib/util/convertBits');

// errors thrown by the library
libnexajs.errors = require('./lib/errors');

// main bitcoin library
libnexajs.Address = require('./lib/address');
libnexajs.GroupToken = require('./lib/grouptoken');
libnexajs.Block = require('./lib/block');
libnexajs.MerkleBlock = require('./lib/block/merkleblock');
libnexajs.BlockHeader = require('./lib/block/blockheader');
libnexajs.HDPrivateKey = require('./lib/hdprivatekey.js');
libnexajs.HDPublicKey = require('./lib/hdpublickey.js');
libnexajs.Message = require('./lib/message');
libnexajs.Networks = require('./lib/networks');
libnexajs.Opcode = require('./lib/opcode');
libnexajs.PrivateKey = require('./lib/privatekey');
libnexajs.PublicKey = require('./lib/publickey');
libnexajs.Script = require('./lib/script/script');
libnexajs.ScriptInterpreter = require('./lib/script/interpreter');
libnexajs.Transaction = require('./lib/transaction');
libnexajs.URI = require('./lib/uri');
libnexajs.Unit = require('./lib/unit');

// dependencies, subject to change
libnexajs.deps = {};
libnexajs.deps.bnjs = require('bn.js');
libnexajs.deps.bs58 = require('bs58');
libnexajs.deps.Buffer = Buffer;
libnexajs.deps.elliptic = require('elliptic');
libnexajs.deps._ = require('lodash');

// Internal usage, exposed for testing/advanced tweaking
libnexajs.Transaction.sighash = require('./lib/transaction/sighash');
